import React from 'react';
import { cn } from '../lib/utils';

export default function VoiceWave({ isListening, className }) {
  // Create a simpler Siri-like animation
  return (
    <div className={cn(
      "relative w-full h-24 mx-auto rounded-lg flex items-center justify-center overflow-hidden",
      isListening ? "bg-blue-50" : "bg-slate-50",
      className
    )}>
      {/* Main pulsing circle */}
      <div 
        className={cn(
          "w-16 h-16 rounded-full transition-all duration-300",
          isListening ? "bg-blue-500 ripple-effect" : "bg-blue-200"
        )}
      />

      {/* The circular dots around the center (Siri-style) */}
      {isListening && (
        <div className="absolute w-28 h-28">
          {[...Array(12)].map((_, index) => (
            <div
              key={index}
              className="absolute w-2 h-2 bg-blue-400 rounded-full"
              style={{
                transform: `rotate(${index * 30}deg) translateY(-16px)`,
                animation: `pulse 1.5s ease-in-out infinite alternate`,
                animationDelay: `${index * 0.1}s`,
              }}
            />
          ))}
        </div>
      )}
    </div>
  );
}
