import React, { useState, useEffect } from 'react';
import { useSpeechRecognition } from './components/SpeechRecognition';
import { Card, CardContent, CardHeader, CardTitle, CardDescription, CardFooter } from './components/ui/card';
import { Button } from './components/ui/button';
import VoiceWave from './components/VoiceWave';
import ChatHistory from './components/ChatHistory';
import { Mic, MicOff } from 'lucide-react';

function App() {
  // Speech recognition hook
  const { transcript, isListening, toggleListening, resetTranscript } = useSpeechRecognition();
  const [chats, setChats] = useState([
    { id: 1, text: "Hello, how can I help you today?", isUser: false },
  ]);

  useEffect(() => {
    // Process transcript when user stops speaking
    if (!isListening && transcript) {
      // Add user message
      const userMessage = {
        id: Date.now(),
        text: transcript,
        isUser: true
      };

      // Add bot response (mock)
      const botMessage = {
        id: Date.now() + 1,
        text: getAssistantResponse(transcript),
        isUser: false
      };

      setChats(prev => [...prev, userMessage, botMessage]);
      resetTranscript();
    }
  }, [isListening, transcript]);

  // Mock response generator
  const getAssistantResponse = (text) => {
    text = text.toLowerCase();

    if (text.includes('weather')) {
      return "It's currently 72°F and sunny in your location.";
    } else if (text.includes('reminder') || text.includes('meeting')) {
      return "I've set a reminder for your meeting.";
    } else if (text.includes('time')) {
      return `The current time is ${new Date().toLocaleTimeString()}.`;
    } else if (text.includes('hello') || text.includes('hi')) {
      return "Hello! How can I help you today?";
    } else {
      return "I'm not sure how to help with that yet. Is there anything else you'd like to know?";
    }
  };

  return (
    <div className="flex h-screen bg-slate-50">
      {/* Sidebar */}
      <aside className="w-80 border-r border-slate-200 bg-white hidden lg:block">
        <div className="p-4 border-b border-slate-200">
          <h2 className="text-xl font-semibold text-slate-900">Chat History</h2>
        </div>
        <ChatHistory chats={chats} />
      </aside>

      {/* Main Content */}
      <main className="flex-1 flex flex-col items-center justify-center p-6 bg-gradient-to-b from-slate-50 to-blue-50">
        <Card className="w-full max-w-md shadow-xl border-0 bg-white/90 backdrop-blur-sm">
          <CardHeader className="text-center pb-2">
            <CardTitle className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 text-transparent bg-clip-text">Voice Assistant</CardTitle>
            <CardDescription className="text-slate-600">
              {isListening 
                ? "Listening to you..." 
                : "Tap the mic to start speaking"}
            </CardDescription>
          </CardHeader>

          <CardContent className="space-y-6">
            {/* Voice Visualization */}
            <VoiceWave isListening={isListening} className="mb-4" />

            {/* Current Transcript */}
            {isListening && transcript && (
              <div className="p-4 bg-blue-50 text-blue-800 rounded-lg border border-blue-100 shadow-sm">
                <p className="italic text-sm text-blue-400 mb-1">I heard:</p>
                "{transcript}"
              </div>
            )}

            {/* Mobile Chat History (visible on small screens) */}
            <div className="lg:hidden">
              <div className="flex items-center justify-between mb-3">
                <h3 className="text-sm font-medium text-slate-500">Recent Conversation</h3>
                {chats.length > 1 && (
                  <button className="text-xs text-blue-500 hover:underline">
                    View All
                  </button>
                )}
              </div>
              <div className="space-y-3 max-h-60 overflow-y-auto pr-1">
                {chats.slice(-4).map(chat => (
                  <div
                    key={chat.id}
                    className={`p-3 rounded-lg shadow-sm ${
                      chat.isUser
                        ? "bg-gradient-to-r from-blue-500 to-blue-600 text-white ml-auto rounded-br-none max-w-[80%]"
                        : "bg-white border border-slate-100 text-slate-800 rounded-bl-none max-w-[80%]"
                    }`}
                  >
                    {chat.text}
                  </div>
                ))}
              </div>
            </div>
          </CardContent>

          <CardFooter className="flex justify-center pb-8 pt-2">
            <Button 
              variant="primary"
              size="circle"
              onClick={toggleListening}
              className={`shadow-xl ${
                isListening 
                  ? 'bg-red-500 hover:bg-red-600 ripple-effect' 
                  : 'bg-gradient-to-r from-blue-500 to-blue-600 hover:from-blue-600 hover:to-blue-700'
              }`}
            >
              {isListening ? <MicOff className="h-6 w-6" /> : <Mic className="h-6 w-6" />}
            </Button>
          </CardFooter>
        </Card>
      </main>
    </div>
  );
}

export default App;
