import React, { useState, useEffect, useRef } from 'react';
import { v4 as uuidv4 } from 'uuid';
import { ChatMessage } from '../types/chat';
import VoiceAnimation from './VoiceAnimation';
import ChatHistory from './ChatHistory';
import { Button } from './ui/button';
import { createSpeechRecognition } from '../utils/speechRecognition';
import { Mic, MicOff } from 'lucide-react';

const VoiceAssistant: React.FC = () => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const recognitionRef = useRef<any>(null);
  const isFinalTranscriptRef = useRef(false);

  useEffect(() => {
    // Initialize speech recognition
    recognitionRef.current = createSpeechRecognition();

    if (recognitionRef.current) {
      recognitionRef.current.onresult = (event: any) => {
        let interimTranscript = '';
        let finalTranscript = '';

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript;
            isFinalTranscriptRef.current = true;
          } else {
            interimTranscript += transcript;
          }
        }

        setTranscript(finalTranscript || interimTranscript);
      };

      recognitionRef.current.onend = () => {
        if (isFinalTranscriptRef.current && transcript) {
          addMessage(transcript, 'user');

          // Simulate assistant response after a delay
          setTimeout(() => {
            addMessage("I'm your voice assistant. How can I help you today?", 'assistant');
          }, 1000);

          setTranscript('');
          isFinalTranscriptRef.current = false;
        }

        setIsListening(false);
      };
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.abort();
      }
    };
  }, [transcript]);

  const toggleListening = () => {
    if (isListening) {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    } else {
      if (recognitionRef.current) {
        recognitionRef.current.start();
        setIsListening(true);
      } else {
        alert('Speech recognition not supported in your browser');
      }
    }
  };

  const addMessage = (content: string, type: 'user' | 'assistant') => {
    const newMessage: ChatMessage = {
      id: uuidv4(),
      content,
      type,
      timestamp: new Date(),
    };

    setMessages((prevMessages) => [...prevMessages, newMessage]);
  };

  return (
    <div className="flex h-full">
      {/* Chat History Panel */}
      <div className="hidden md:block md:w-1/3 border-r border-gray-200 h-full">
        <div className="p-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold">Conversation History</h2>
        </div>
        <div className="h-[calc(100%-60px)]">
          <ChatHistory messages={messages} />
        </div>
      </div>

      {/* Main Voice Assistant Interface */}
      <div className="flex-1 flex flex-col h-full">
        <div className="flex-1 flex flex-col items-center justify-center p-6">
          <div className="w-full max-w-md flex flex-col items-center">
            <div className="mb-8 text-center">
              <h1 className="text-2xl font-bold mb-2">Voice Assistant</h1>
              <p className="text-gray-500">
                {isListening ? "I'm listening..." : "Press the mic button to speak"}
              </p>
            </div>

            <VoiceAnimation isListening={isListening} className="mb-6" />

            {transcript && (
              <div className="w-full bg-gray-100 rounded-lg p-4 mb-6 text-center">
                <p>{transcript}</p>
              </div>
            )}

            <Button
              size="lg"
              className="rounded-full w-16 h-16 flex items-center justify-center"
              onClick={toggleListening}
            >
              {isListening ? <MicOff size={24} /> : <Mic size={24} />}
            </Button>
          </div>
        </div>

        {/* Mobile View - Chat History */}
        <div className="md:hidden h-1/3 border-t border-gray-200">
          <div className="p-2 border-b border-gray-200">
            <h2 className="text-sm font-semibold">Conversation History</h2>
          </div>
          <div className="h-[calc(100%-40px)]">
            <ChatHistory messages={messages} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default VoiceAssistant;
