import React, { useState, useEffect } from 'react';
import { cn } from '../utils/cn';

interface VoiceAnimationProps {
  isListening: boolean;
  className?: string;
}

const VoiceAnimation: React.FC<VoiceAnimationProps> = ({
  isListening,
  className,
}) => {
  const [bars, setBars] = useState<number[]>([]);

  // Generate random heights for the bars
  useEffect(() => {
    const generateBars = () => {
      const total = 30; // Number of bars
      return Array.from({ length: total }, () => 
        isListening ? Math.floor(Math.random() * 50) + 10 : 10
      );
    };

    setBars(generateBars());

    let interval: NodeJS.Timeout;
    if (isListening) {
      interval = setInterval(() => {
        setBars(generateBars());
      }, 100);
    }

    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isListening]);

  return (
    <div className={cn('flex items-end justify-center h-24 gap-[2px]', className)}>
      {bars.map((height, index) => (
        <div
          key={index}
          className={cn(
            'w-[3px] rounded-t-full transition-all duration-100',
            isListening ? 'bg-blue-500' : 'bg-gray-300'
          )}
          style={{ height: `${height}%` }}
        />
      ))}
    </div>
  );
};

export default VoiceAnimation;
