import React, { useState, useEffect } from 'react';
import { cn } from '../lib/utils';

export default function VoiceWave({ isListening, className }) {
  const [bubbles, setBubbles] = useState([]);

  useEffect(() => {
    const generateBubbles = () => {
      if (!isListening) {
        setBubbles([]);
        return;
      }

      // Generate 4 to 8 bubbles with random properties
      const numberOfBubbles = Math.floor(Math.random() * 5) + 4;
      const newBubbles = [];

      for (let i = 0; i < numberOfBubbles; i++) {
        newBubbles.push({
          id: Math.random(),
          size: Math.floor(Math.random() * 30) + 10, // 10px to 40px
          position: {
            x: Math.random() * 100, // 0% to 100% of container width
            y: Math.random() * 100  // 0% to 100% of container height
          },
          opacity: Math.random() * 0.5 + 0.5, // 0.5 to 1.0 opacity
          animationDuration: Math.random() * 1 + 0.5 // 0.5s to 1.5s
        });
      }

      setBubbles(newBubbles);
    };

    // Generate initial bubbles
    generateBubbles();

    // Update bubbles periodically when listening
    let intervalId;
    if (isListening) {
      intervalId = setInterval(generateBubbles, 300);
    }

    return () => {
      if (intervalId) clearInterval(intervalId);
    };
  }, [isListening]);

  return (
    <div className={cn(
      "relative w-full h-32 mx-auto rounded-full flex items-center justify-center",
      isListening ? "bg-blue-50" : "bg-slate-50",
      className
    )}>
      {/* Central pulsing circle (like Google Assistant) */}
      {isListening && (
        <div className="absolute w-16 h-16 bg-blue-500 rounded-full opacity-80 animate-pulse z-10" />
      )}

      {/* Floating bubbles */}
      {bubbles.map((bubble) => (
        <div
          key={bubble.id}
          className="absolute rounded-full bg-blue-500"
          style={{
            width: `${bubble.size}px`,
            height: `${bubble.size}px`,
            left: `${bubble.position.x}%`,
            top: `${bubble.position.y}%`,
            opacity: bubble.opacity,
            transition: `all ${bubble.animationDuration}s ease-in-out`,
            transform: isListening ? 'scale(1)' : 'scale(0)',
          }}
        />
      ))}

      {/* Small dots around the center (like Siri) */}
      {isListening && (
        <div className="absolute w-24 h-24 rounded-full flex items-center justify-center">
          {[...Array(12)].map((_, index) => (
            <div
              key={index}
              className="absolute w-2 h-2 bg-blue-400 rounded-full"
              style={{
                transform: `rotate(${index * 30}deg) translateY(-14px)`,
                animation: `pulse ${0.8 + (index % 3) * 0.2}s ease-in-out infinite alternate`,
                opacity: 0.6 + (index % 3) * 0.2
              }}
            />
          ))}
        </div>
      )}
    </div>
  );
}
