import React from 'react';
import { ScrollArea } from './ui/scroll-area';
import { cn } from '../lib/utils';

export default function ChatHistory({ chats, className }) {
  return (
    <ScrollArea className={cn("h-[calc(100vh-5rem)]", className)}>
      <div className="p-4 space-y-4">
        {chats.map((chat, index) => (
          <div key={chat.id} className="relative">
            {/* Time indicator (display only for first message or when switching speakers) */}
            {(index === 0 || (index > 0 && chats[index-1].isUser !== chat.isUser)) && (
              <div className="text-xs text-slate-400 mb-1 px-2">
                {new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
              </div>
            )}

            <div
              className={cn(
                "max-w-[85%] p-3 rounded-lg shadow-sm transition-all duration-200 hover:shadow-md",
                chat.isUser
                  ? "bg-gradient-to-r from-blue-500 to-blue-600 text-white ml-auto rounded-br-none"
                  : "bg-white border border-slate-100 text-slate-800 rounded-bl-none"
              )}
            >
              {chat.text}
            </div>

            {/* User or assistant indicator */}
            <div 
              className={cn(
                "absolute bottom-0 text-xs text-slate-400",
                chat.isUser ? "right-0 mr-2 mb-[-1.25rem]" : "left-0 ml-2 mb-[-1.25rem]"
              )}
            >
              {chat.isUser ? 'You' : 'Assistant'}
            </div>
          </div>
        ))}
      </div>
    </ScrollArea>
  );
}
