import React, { useState, useEffect } from 'react';
import { cn } from '../lib/utils';

export default function VoiceWave({ isListening, className }) {
  const [bars, setBars] = useState([]);

  useEffect(() => {
    const generateBars = () => {
      const numberOfBars = 30;
      const newBars = [];

      for (let i = 0; i < numberOfBars; i++) {
        const height = isListening ? Math.floor(Math.random() * 40) + 5 : 5;
        newBars.push(height);
      }

      setBars(newBars);
    };

    generateBars();

    // Only animate when listening
    let intervalId;
    if (isListening) {
      intervalId = setInterval(generateBars, 100);
    }

    return () => {
      if (intervalId) clearInterval(intervalId);
    };
  }, [isListening]);

  return (
    <div className={cn("flex items-end justify-center h-16 gap-[2px]", className)}>
      {bars.map((height, index) => (
        <div
          key={index}
          className={cn(
            "w-1 rounded-t-full transition-all duration-100",
            isListening ? "bg-blue-500" : "bg-gray-300"
          )}
          style={{ height: `${height}px` }}
        />
      ))}
    </div>
  );
}
